const Main = ({ navigation }) => {
  return (
    <Tab.Navigator
      // screenOptions={{
      //   tabBarShowLabel: false,
      //   headerShown: false,
      //   tabBarStyle: { ...styles.tab },
      //   tabBarHideOnKeyboard: true,
      // }}
    >
      <Tab.Screen name="Mall Page" component={MallPage}></Tab.Screen>
      <Tab.Screen
        name="Contact Page"
        component={ContactPage}
        // options={{ tabBarButton: (props) => <CustomTab {...props} /> }}
      ></Tab.Screen>
      <Tab.Screen name="Cart" component={CartPage}></Tab.Screen>
    </Tab.Navigator>
  );
};
export default Main;
const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: "center",
    alignContent: "center",
  },
  text: {
    justifyContent: "center",
    alignContent: "center",

    fontWeight: "bold",
    fontSize: 20,
  },
  tab: {
    position: "absolute",
    bottom: 25,
    left: 20,
    right: 20,
    elevation: 0,
    backgroundColor: "#ffffff",
    borderRadius: 25,
    height: 90,
  },
  main: {
    backgroundColor: "#009387",
  },
});
