import React from "react";
import { Text, View, StyleSheet } from "react-native";


import { NavigationContainer } from "@react-navigation/native";
import { createBottomTabNavigator } from "@react-navigation/bottom-tabs";

import MallPage from "./Childer/MallPage/MallPage";
import ContactPage from "./Childer/Contactpage/ContactPage";
import CartPage from "./Childer/Cartpage/CartPage";

const Tab = createBottomTabNavigator();

const Main = ({ navigation }) => {
  return (
    <NavigationContainer>
      <Tab.Navigator
        screenOptions={() => ({
          tabBarActiveTintColor: "ffa000",
          tabBarInactiveTintColor: "fff",
          tabBarIndicatorStyle: { backgroundColor: "#ffa000" },
          tabBarLabelStyle: { fontWeight: "bold", fontSize: 10 },
          tabBarItemStyle: { width: 100 },
          tabBarStyle: { backgroundColor: "#9c27b0" },
          headerShown: false,
        })}
      >
        <Tab.Screen name="Mall Page" component={MallPage}></Tab.Screen>
        <Tab.Screen name="Contact Page" component={ContactPage}></Tab.Screen>
        <Tab.Screen name="Cart" component={CartPage}></Tab.Screen>
      </Tab.Navigator>
    </NavigationContainer>
  );
};
export default Main;
const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: "center",
    alignContent: "center",
  },
  text: {
    justifyContent: "center",
    alignContent: "center",

    fontWeight: "bold",
    fontSize: 20,
  },
});
