import React, { Component } from "react";
import { View, Text, StyleSheet, TextInput } from "react-native";


// create a component
const MallHeader = () => {
  return (
    <View style={styles.container}>
     <TextInput style={styles.textInput} placeholder="Nike, Puma, Adidas, Human Heart,....." />
      
    </View>
  );
};

// define your styles
const styles = StyleSheet.create({
  container: {
    flex:1,
    
    justifyContent: 'center',
        alignItems: 'center',
    backgroundColor:'#e32f45'
  },
  textInput:{
    marginTop:30,
    borderColor:"#fff",
    borderWidth:2,
    height:40,
    borderRadius:10,
    width:"60%",
    padding:10,


  }
});

//make this component available to the app
export default MallHeader;
