import React from "react";
import { Text, View, StyleSheet } from "react-native";
import Ionicons from "react-native-vector-icons/FontAwesome";

import { NavigationContainer } from "@react-navigation/native";
import { createBottomTabNavigator } from "@react-navigation/bottom-tabs";
import Home from "../../Home/Home";
import Login from "../Login/Login";
import Forgot from "../Login/Forgot";
import MaillPage from "./ChildPage/MallPage";

const Tab = createBottomTabNavigator();

const Main = ({ navigation }) => {
  return (
    <NavigationContainer>
      <Tab.Navigator
        screenOptions={() => ({
          tabBarActiveTintColor: "ffa000",
          tabBarInactiveTintColor: "fff",
          tabBarIndicatorStyle: { backgroundColor: "#ffa000" },
          tabBarLabelStyle: { fontWeight: "bold", fontSize: 10 },
          tabBarItemStyle: { width: 100 },
          tabBarStyle: { backgroundColor: "#9c27b0" },
        })}
      >
        <Tab.Screen name="Mall Page" component={MaillPage}></Tab.Screen>
        <Tab.Screen name="Login" component={Login}></Tab.Screen>
        <Tab.Screen name="Forgot" component={Forgot}></Tab.Screen>
      </Tab.Navigator>
    </NavigationContainer>
  );
};
export default Main;
const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: "center",
    alignContent: "center",
  },
  text: {
    justifyContent: "center",
    alignContent: "center",

    fontWeight: "bold",
    fontSize: 20,
  },
});
