import React, { useState } from "react";
import {
  Text,
  View,
  StyleSheet,
  Image,
  TouchableOpacity,
  Platform,
} from "react-native";
import * as ImagePicker from "expo-image-picker";

import { firebase } from "../config/firebase";

import {
  getStorage,
  uploadString,
  uploadBytesResumable,
  getDownloadURL,
  ref,
} from "firebase/storage";
import { async } from "@firebase/util";

// create a component
const ContactHeader = () => {
///
const [selectedImage, setSelectedImage] = useState({
    localURI:
      "https://firebasestorage.googleapis.com/v0/b/rrdemo-98424.appspot.com/o/z3777176298214_0f68ce3fa3ad50c82e029ab4a81c0fe4.jpg?alt=media&token=3f76a4c2-3eaa-4250-b6d4-a95f2258bdd5",
  });
  const openImage = async () => {
    let result = await ImagePicker.launchImageLibraryAsync({ base64: true });
    if (result.cancelled) return;
    let uri = result.uri;
    setSelectedImage({ localURI: uri });
    console.log(uri);

    if (Platform.OS === "web") {
      let base64code = result.base64;
      await uploadBase64Code(base64code);
      console.log("done");
    } else {
      let uri = result.uri;
      //convert to blob file

      const blobfile = await convertToBlob(uri);
      console.log(blobfile);
      //upload file
      await uploadFileBlob(blobfile);
    }
  };
  ///



  
    ///
  return (
    <View style={styles.container}>
      <View style={styles.avatar}>

        <Image
            style={styles.imgavatar}
            source={require("../../../../assets/logo.png")}
          />
   
        
      </View>
      <View style={styles.textavatar}>
          <TouchableOpacity style={styles.btn}>
            <Text>Choose your image</Text>
          </TouchableOpacity>
        </View>
    </View>
  );
};

// define your styles
const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#fff",
    justifyContent: "center",
    alignItems: "center",
  },
  avatar:{
    padding:10,
    backgroundColor:"black",
    borderRadius:60,
    margin:20,
  },

  imgavatar:{
    justifyContent: "center",
    alignItems: "center",

    width:100,
    height:100,
    borderRadius:50,
  },
  textavatar: {
    color: "#fff",
  },
  btn: {},
});

//make this component available to the app
export default ContactHeader;
