//import liraries
import React, { Component } from "react";
import {
  View,
  Text,
  StyleSheet,
  TextInput,
  TouchableOpacity,
} from "react-native";
import FontAwesome from "react-native-vector-icons/FontAwesome";
import CartPage from "../Cartpage/CartPage";

// create a component
const MallHeader = () => {
  return (
    <View style={styles.container}>
      <View style={styles.header}>
        <TextInput
          style={styles.textInput}
          placeholder="Nike, Puma, Adidas, Human Heart,....."
        />
        <TouchableOpacity>
          <FontAwesome
            style={styles.icons}
            name="search"
            color="#fff"
            size={25}
          />
        </TouchableOpacity>
        <TouchableOpacity>
          <FontAwesome
            style={{ marginTop: 30, left: 25 }}
            name="shopping-bag"
            color="#fff"
            size={30}
          />
        </TouchableOpacity>
      </View>
    </View>
  );
};

// define your styles
const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: "center",
    alignItems: "center",
    backgroundColor: "#e32f45",
    borderRadius:30,
  },
  header: {
    justifyContent: "center",
    alignItems: "center",

    flexDirection: "row",
  },
  icons: {
    marginTop: 30,
    left: 5,
  },
  textInput: {
    right: 7,
    marginTop: 30,
    borderColor: "#fff",
    borderWidth: 2,
    height: 40,
    borderRadius: 10,
    width: "60%",
    padding: 10,
  },
});

//make this component available to the app
export default MallHeader;
